import { useEffect, useState } from "react";
import axios from "axios";

function Exchanges() {
  const [exchanges, setExchanges] = useState([]);
  const [filter, setFilter] = useState("all");
  
  const userId = localStorage.getItem("userId");
  
  useEffect(() => {
    const fetchExchanges = async () => {
      try {
        const token = localStorage.getItem("token");
        const res = await axios.get("http://localhost:5000/api/exchanges", {
          headers: { Authorization: `Bearer ${token}` }
        });
        setExchanges(res.data);
      } catch (err) {
        console.error(err);
      }
    };
    fetchExchanges();
  }, []);

  const handleAction = async (id, status) => {
    try {
      const token = localStorage.getItem("token");
      await axios.put(
        `http://localhost:5000/api/exchanges/${id}`,
        { status },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setExchanges((prev) =>
        prev.map((e) => (e.id === id ? { ...e, status } : e))
      );
    } catch (err) {
      console.error(err);
    }
  };

  
  const filteredExchanges = exchanges.filter((ex) => {
    if (filter === "sent") return ex.sender_id == userId;
    if (filter === "received") return ex.received_id == userId;
    return true;
  });


  return (
    <div>
      <h2>Solicitudes de intercambio</h2>
      {exchanges.map((ex) => (
        <div key={ex.id} className="exchange-card">
            <p>
                <b>{ex.sender_name}</b> quiere intercambiar la habilidad de <b>{ex.skill_offered}</b>{" "}
                por la de <b>{ex.skill_requested}</b>
            </p>
            <p>Estado: {ex.status}</p>
            {ex.status === "pending" && (
                <div>
                    <button 
                    className="bg-green-500 text-white px-3 py-1 rounded-md mr-2"
                    onClick={() => handleAction(ex.id, "accepted")}
                    >
                    Aceptar
                    </button>
                    <button 
                    className="bg-red-500 text-white px-3 py-1 rounded-md"
                    onClick={() => handleAction(ex.id, "rejected")}
                    >
                    Rechazar
                    </button>
                </div>
            )}
        </div>
      ))}
    </div>
  );
}

export default Exchanges;
