import { useState } from "react";
import axios from "axios";

function Search({ currentUser }) {
  const [skill, setSkill] = useState("");
  const [type, setType] = useState("");
  const [location, setLocation] = useState("");
  const [results, setResults] = useState([]);

  const handleSearch = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/search", {
        params: { skill, type, location, userId: currentUser.id },
      });
      setResults(res.data);
    } catch (err) {
      console.error("Error buscando:", err);
    }
  };

  return (
    <div className="max-w-4xl mx-auto p-6">
      <h2 className="text-2xl font-bold mb-4">Buscar Habilidades</h2>

      {/* Filtros */}
      <div className="flex gap-4 mb-6">
        <input
          type="text"
          placeholder="Habilidad (ej. guitarra)"
          value={skill}
          onChange={(e) => setSkill(e.target.value)}
          className="border rounded-lg p-2 w-1/3"
        />

        <select
          value={type}
          onChange={(e) => setType(e.target.value)}
          className="border rounded-lg p-2 w-1/3"
        >
          <option value="">Todos</option>
          <option value="offer">Ofertadas</option>
          <option value="want">Buscadas</option>
        </select>

        <input
          type="text"
          placeholder="UbicaciÃ³n"
          value={location}
          onChange={(e) => setLocation(e.target.value)}
          className="border rounded-lg p-2 w-1/3"
        />
      </div>

      <button
        onClick={handleSearch}
        className="bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700"
      >
        Buscar
      </button>

      {/* Resultados */}
      <div className="mt-6 space-y-4">
        {results.map((r, idx) => (
          <div
            key={idx}
            className="p-4 bg-white shadow-md rounded-lg border flex items-center gap-4"
          >
            <img
              src={r.avatar || `https://via.placeholder.com/50/007bff/ffffff?text=${r.name[0].toUpperCase()}`}
              alt={r.name}
              className="w-12 h-12 rounded-full object-cover"
            />
            <div>
              <h3 className="font-semibold">{r.name}</h3>
              <p className="text-sm text-gray-600">{r.location}</p>
              <p className="text-blue-600 text-sm">
                {r.type === "offer" ? "Ofrece: " : "Busca: "} {r.skill_name}
              </p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Search;
