// src/pages/ChatList.jsx
import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import Navbar from "../components/Navbar";

function ChatList() {
  const [conversations, setConversations] = useState([]);
  const [loading, setLoading] = useState(true);
  //const location = useLocation();
  //const { userId } = location.state || {};
  const navigate = useNavigate();

  useEffect(() => {
    const fetchConversations = async () => {
      try {
        const userId = localStorage.getItem("userId");
        const res = await axios.get(`http://localhost:5000/api/chats/${userId}`);
        setConversations(res.data);
      } catch (err) {
        console.error("Error al obtener las conversaciones:", err);
      } finally {
        setLoading(false);
      }
    };

    fetchConversations();
  }, []);

  const handleOpenChat = (chatId, partnerName, partnerId) => {
    navigate(`/chat/${chatId}`, { state: { partnerName, receiverId: partnerId } });
  };

  if (loading) {
    return (
      <div className="flex justify-center items-center h-screen">
        <p className="text-gray-600">Cargando conversaciones...</p>
      </div>
    );
  }

  return (
    <>
      <Navbar />
      <div className="max-w-3xl mx-auto mt-10 p-4">
        <h2 className="text-2xl font-bold mb-6 text-center">Mis Conversaciones</h2>

        {conversations.length === 0 ? (
          <p className="text-center text-gray-500">
            No tienes conversaciones todavía.
          </p>
        ) : (
          <ul className="divide-y divide-gray-200">
            {conversations.map((conv) => (
              <li
                key={conv._id}
                onClick={() => handleOpenChat(conv._id, conv.partnerName, conv.partnerId)}
                className="p-4 cursor-pointer hover:bg-gray-100 rounded-lg flex justify-between items-center"
              >
                <div>
                  <h3 className="text-lg font-semibold">{conv.partnerName}</h3>
                  <p className="text-sm text-gray-500">
                    Último mensaje: {conv.lastMessage || "Sin mensajes aún"}
                  </p>
                </div>
                <span className="text-xs text-gray-400">
                  {new Date(conv.updatedAt).toLocaleDateString()}
                </span>
              </li>
            ))}
          </ul>
        )}
      </div>
    </>
  );
}

export default ChatList;
