import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import Navbar from "../components/Navbar";
import UserAvatar from "../components/UserAvatar";
import Skillcard from "../components/Skillcard";
import { FaEdit, FaPlus } from "react-icons/fa";


function Profile() {
  const [profile, setProfile] = useState(null);
  const [editingBio, setEditingBio] = useState(false);
  const [newBio, setNewBio] = useState("");
  const [editingLocation, setEditingLocation] = useState(false);
  const [newLocation, setNewLocation] = useState("");

  const navigate = useNavigate();

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const token = localStorage.getItem("token");
        console.log("token en frontend: ", token); //
        console.log("headers enviados:", { Authorization: `Bearer ${token}` }); //Se esta enviando?
        const res = await axios.get("http://localhost:5000/api/profile/me", {
          headers: { Authorization: `Bearer ${token}` }
        });
        
        setProfile(res.data);
        setNewBio(res.data.bio || "");
        setNewLocation(res.data.location || "");
      } catch (err) {
        console.error("Error obteniendo perfil", err);
      }
    };

    fetchProfile();
  }, []);

  if (!profile) return <p>Cargando perfil...</p>;


  const saveBio = async () => {
    try {
      const token = localStorage.getItem("token");
      await axios.put(
        "http://localhost:5000/api/profile/bio",
        { bio: newBio },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setProfile({ ...profile, bio: newBio });
      setEditingBio(false);
    } catch (err) {
      console.error("Error guardando bio:", err);
    }
  };

    const saveLocation = async () => {
    try {
      const token = localStorage.getItem("token");
      await axios.put(
        "http://localhost:5000/api/profile/location",
        { location: newLocation },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setUser({ ...profile, location: newLocation });
      setEditingLocation(false);
    } catch (err) {
      console.error("Error guardando ubicación:", err);
    }
  };

  return (
    <div className="min-h-screen bg-gray-100">
      <Navbar />
      <div className="max-w-4xl mx-auto p-6">
        {/* Avatar e información */}
        <div className="flex items-center gap-4 bg-white shadow-md rounded-xl p-6 mb-6">
          <UserAvatar name={profile.name} image={profile.avatar} />
          <div>
            <h2 className="text-2xl font-bold">{profile.name}</h2>
            <p className="text-gray-600">{profile.email}</p>
            
            <div className="flex justify-between items-center">
              {editingLocation ? (
                <div className="flex gap-2 items-center">
                  <input
                    type="text"
                    value={newLocation}
                    onChange={(e) => setNewLocation(e.target.value)}
                    className="border rounded-lg px-2 py-1"
                  />
                  <button
                    onClick={saveLocation}
                    className="bg-blue-600 text-white px-3 py-1 rounded-lg"
                  >
                    Guardar
                  </button>
                </div>
              ) : (
                <p className="text-gray-500 flex items-center gap-2">
                  {pro.location || "Ubicación no definida"}
                  <button
                    onClick={() => setEditingLocation(true)}
                    className="text-gray-500 hover:text-blue-600"
                  >
                    <FaEdit />
                  </button>
                </p>
              )}
            </div>
          </div>
        </div>

        {/* Biografía */}
        <div className="bg-white shadow-md rounded-xl p-6 mb-6 relative">
          <h3 className="text-lg font-semibold mb-3 flex justify-between items-center">
            Biografía
            <button
              onClick={() => setEditingBio(!editingBio)}
              className="text-gray-500 hover:text-blue-600"
            >
              <FaEdit />
            </button>
          </h3>

          {editingBio ? (
            <div>
              <textarea
                value={newBio}
                onChange={(e) => setNewBio(e.target.value)}
                className="w-full border rounded-lg p-2 mb-2"
              />
              <button
                onClick={saveBio}
                className="bg-blue-600 text-white px-4 py-1 rounded-lg hover:bg-blue-700"
              >
                Guardar
              </button>
            </div>
          ) : (
            <p className="text-gray-700">
              {profile.bio && profile.bio.trim() !== ""
                ? profile.bio
                : "Aún no has escrito tu biografía"}
            </p>
          )}
        </div>

        {/* Habilidades */}
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          <Skillcard title="Habilidades Ofertadas" skills={profile.skillsOffered || []} addSkillButton/>
          <Skillcard title="Habilidades Buscadas" skills={profile.skillsWanted || []} addSkillButton />
        </div>

        {/* Botón editar */}
        <div className="mt-6 text-center">
          <button
            onClick={() => navigate("/edit-profile")}
            className="bg-blue-600 text-white px-6 py-2 rounded-lg hover:bg-blue-700"
          >
            ⚙️ Editar Perfil
          </button>
        </div>
      </div>
    </div>
  );
}

export default Profile;
