import { useState, useEffect, useRef } from "react";
import { useParams, useLocation, useNavigate } from "react-router-dom";
import { io } from "socket.io-client";
import axios from "axios";
import Navbar from "../components/Navbar";

function Chat() {
  const { exchangeId } = useParams();
  const { state } = useLocation();
  const navigate = useNavigate();
  const socketRef = useRef(null);
  const messagesEndRef = useRef(null);

  const [messages, setMessages] = useState([]);
  const [message, setMessage] = useState("");
  const [loading, setLoading] = useState(true);

  const userId = localStorage.getItem("userId");
  const receiverId = state?.receiverId;
  const partnerName = state?.partnerName || "Chat";

  // Desplazar al último mensaje
  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  // Conexión a la sala y carga de mensajes
  useEffect(() => {
    const socket = io("http://localhost:5000");
    socketRef.current = socket;

    // Unirse a la sala
    socket.emit("join_room", exchangeId);

    // Cargar historial
    const fetchMessages = async () => {
      try {
        const token = localStorage.getItem("token");
        const res = await axios.get(
          `http://localhost:5000/api/messages/${exchangeId}`,
          { headers: { Authorization: `Bearer ${token}` } }
        );
        setMessages(res.data);
      } catch (err) {
        console.error("Error al cargar mensajes:", err);
      } finally {
        setLoading(false);
      }
    };

    fetchMessages();

    // Escuchar nuevos mensajes
    socket.on("receive_message", (data) => {
      if (data.exchangeId === exchangeId) {
        setMessages((prev) => [...prev, data]);
      }
    });

    return () => {
      socket.disconnect();
    };
  }, [exchangeId]);

  // Scroll automático
  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  // Enviar mensaje
  const sendMessage = () => {
    if (!message.trim()) return;

    const msgData = {
      exchangeId,
      senderId: userId,
      receiverId,
      message,
    };

    socketRef.current.emit("send_message", msgData);
    //setMessages((prev) => [...prev, msgData]);
    setMessage("");
  };

  
useEffect(() => {
  if (!receiverId) {
    // Si no hay receiverId, redirige al listado
    navigate("/chat");
  }
}, [receiverId]);

  return (
    <>
      <Navbar />
      <div className="max-w-2xl mx-auto mt-10 border rounded-lg shadow p-4 bg-white">
        <h2 className="text-lg font-bold mb-2 text-center text-color7">
          Chat con {partnerName}
        </h2>

        <div className="h-96 overflow-y-auto border rounded-md p-2 mb-2 bg-gray-50">
          {loading ? (
            <p className="text-center text-gray-500">Cargando mensajes...</p>
          ) : messages.length === 0 ? (
            <p className="text-center text-gray-400">No hay mensajes aún.</p>
          ) : (
            messages.map((msg, i) => (
              <div
                key={i}
                className={`my-1 p-2 rounded max-w-[80%] ${
                  msg.senderId === userId
                    ? "bg-color7 text-white ml-auto text-right"
                    : "bg-gray-200 text-left"
                }`}
              >
                {msg.message}
              </div>
            ))
          )}
          <div ref={messagesEndRef} />
        </div>

        <div className="flex gap-2">
          <input
            value={message}
            onChange={(e) => setMessage(e.target.value)}
            placeholder="Escribe un mensaje..."
            className="flex-1 border rounded px-2 py-1"
          />
          <button
            onClick={sendMessage}
            className="bg-color6 text-white px-4 py-2 rounded hover:bg-color7"
          >
            Enviar
          </button>
        </div>

        <button
          onClick={() => navigate("/chat")}
          className="mt-3 text-sm text-gray-500 hover:text-gray-700"
        >
          Volver a conversaciones
        </button>
      </div>
    </>
  );
}

export default Chat;
