import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import Navbar from "../components/Navbar";
import UserAvatar from "../components/UserAvatar";
import Skillcard from "../components/Skillcard";
import { FaEdit, FaPlus } from "react-icons/fa";


function Profile() {
  const [profile, setProfile] = useState(null);
  const [editingBio, setEditingBio] = useState(false);
  const [newBio, setNewBio] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const token = localStorage.getItem("token");
        console.log("token en frontend: ", token); //
        console.log("headers enviados:", { Authorization: `Bearer ${token}` }); //Se esta enviando?
        const res = await axios.get("http://localhost:5000/api/profile/me", {
          headers: { Authorization: `Bearer ${token}` }
        });
        
        setProfile(res.data);
        setNewBio(res.data.bio || "");
      } catch (err) {
        console.error("Error obteniendo perfil", err);
      }
    };

    fetchProfile();
  }, []);

  if (!profile) return <p>Cargando perfil...</p>;


  const saveBio = async () => {
    try {
      const token = localStorage.getItem("token");
      await axios.put(
        "http://localhost:5000/api/profile/bio",
        { bio: newBio },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setUser({ ...user, bio: newBio });
      setEditingBio(false);
    } catch (err) {
      console.error("Error guardando bio:", err);
    }
  };
  
  return (
    <div className="min-h-screen bg-gray-100">
      <Navbar />
      <div className="max-w-4xl mx-auto p-6">
        {/* Avatar e información */}
        <div className="flex items-center gap-4 bg-white shadow-md rounded-xl p-6 mb-6">
          <UserAvatar name={profile.name} image={profile.avatar} />
          <div>
            <h2 className="text-2xl font-bold">{profile.name}</h2>
            <p className="text-gray-600">{profile.email}</p>
            <p className="text-gray-500">{profile.location || "Ubicación no definida"}</p>
          </div>
        </div>

        {/* Biografía */}
        <div className="bg-white shadow-md rounded-xl p-6 mb-6">
          <h3 className="text-lg font-semibold mb-3">Biografía</h3>
          <p className="text-gray-700">
            {profile.bio && profile.bio.trim() !== ""
              ? profile.bio
              : "Aún no has compartido tu historia. ¡Anímate a escríbirla!"}
          </p>
        </div>

        {/* Habilidades */}
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          <Skillcard title="Habilidades Ofertadas" skills={profile.skillsOffered || []} />
          <Skillcard title="Habilidades Buscadas" skills={profile.skillsWanted || []} />
        </div>

        {/* Botón editar */}
        <div className="mt-6 text-center">
          <button
            onClick={() => navigate("/edit-profile")}
            className="bg-blue-600 text-white px-6 py-2 rounded-lg hover:bg-blue-700"
          >
            ⚙️ Editar Perfil
          </button>
        </div>
      </div>
    </div>
  );
}

export default Profile;
